mapper.select:
select count(*) from tbl_tran tr left join tbl_customer cus on cus.id=tr.customer_id left join tbl_user u1 on u1.id=tr.owner left join tbl_contacts con on con.id=tr.contacts_id
and cus.name like '%' #{coustomerId} '%'
and u1.owner like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and con.fullname like '%' #{contactId} '%'
and tr.stage =#{stage}
and tr.type =#{type}
and tr.source =#{source}

select
tr.id,u1.name as owner, tr.money, tr.name, tr.expected_date, cus.name as customer_id,dv1.value as stage,dv2.value as type,dv3.value as source, act.name as activity_id, con.fullname as contacts_id,u2.name as create_by, tr.create_time,u3.name as edit_by, tr.edit_time, tr.description, tr.contact_summary, tr.next_contact_time from tbl_tran tr left join tbl_customer cus on cus.id=tr.customer_id left join tbl_user u1 on u1.id=tr.owner left join tbl_user u2 on u2.id=tr.create_by left join tbl_user u3 on u3.id=tr.edit_by left join tbl_contacts con on con.id=tr.contacts_id left join tbl_dic_value dv1 on dv1.id=tr.stage left join tbl_dic_value dv2 on dv2.id=tr.type left join tbl_dic_value dv3 on dv3.id=tr.source left join tbl_activity act on act.id=tr.activity_id
and cus.name like '%' #{coustomerId} '%'
and u1.owner like '%' #{owner} '%'
and tr.name like '%' #{name} '%'
and con.fullname like '%' #{contactId} '%'
and tr.stage =#{stage}
and tr.type =#{type}
and tr.source =#{source}
order by tr.create_time desc limit #{beginNo},#{pageSize}
