select a.id,a.name,u1.name as owner,a.start_date,a.end_date,a.cost,a.description ,a.create_time,u2.name as create_by,u3.name as edit_by from tbl_activity a join tbl_user u1 on u1.id=a.owner join tbl_user u2 on u2.id=a.create_by left join tbl_user u3 on u3.id=a.edit_by
and a.name like '%' #{param_name} '%'
and u1.name like '%' #{param_owner} '%'
and a.start_date>=#{param_start_date}
and a.end_date<= #{param_end_date}
order by a.create_time desc limit #{beginNo},#{pageSize}

